import { Component } from '@angular/core';

@Component({
  selector: 'app-way-data',
  templateUrl: './way-data.component.html',
  styleUrls: ['./way-data.component.css']
})
export class WayDataComponent {
  name:string = '';
  user:string = '';
  senha:string = '';
}
<div class="aula">
    <h1>Two Way Data Binding</h1>
        <form>
    <input type="text" [(ngModel)]="name" name="name" placeholder="Digite seu nome">
    <input type="submit">
        </form>
        <div *ngIf="name !== ''">
            seu nome de usuario é: {{name}}
        </div>
                <h1>Tela de Login</h1>
        <form>
            <input type="text" [(ngModel)]="user" name="user" placeholder="insira seu user">
            <input type="password" [(ngModel)]="senha" name="senha" placeholder="insira sua senha" >
        </form>
        <p *ngIf="user !== '' && senha !== '' else semAcesso">Bem-Vindo {{user}} !!</p>
        <ng-template #semAcesso>
            <p>Usuario e Senha invalidos</p>
        </ng-template>


</div>
