import { Component } from '@angular/core';

@Component({
  selector: 'app-if-render',
  templateUrl: './if-render.component.html',
  styleUrls: ['./if-render.component.css']
})
export class IfRenderComponent {
  cond: boolean = false
  nome: string = "Artu"
  user: string = "Artur"

}
<div class="aula">
    <h2 *ngIf="cond">Messagem</h2>
    <h2 *ngIf="nome === 'Artur'; else semAcesso "> Bem-Vindo: {{ user }} </h2>
    <ng-template #semAcesso>
        <div>
            <h2>Sem acesso p vc -_-</h2>
        </div>
    </ng-template>

</div>
