import { Component } from '@angular/core';
import { Carro } from 'src/app/Carros';
import { ListService } from 'src/app/services/list.service';


@Component({
  selector: 'app-render-list',
  templateUrl: './render-list.component.html',
  styleUrls: ['./render-list.component.css']
})
export class RenderListComponent {

  carros:Carro[]= []

  infoCar = '';

  infos(carro: Carro): void{
    this.infoCar = 'o modelo ${carro.modelo} da marca ${carro.marca} é do ano de ${carro.ano}';
  }
  constructor(private listService:ListService){};
  removeCar(carro: Carro){
    this.carros = this.listService.remove(this.carros, carro)

  }

}
<div class="aula">
        <h1>Meus carros:</h1>
        <ul>
            <li *ngFor="let carro of carros">
                Modelo: {{carro.modelo}} Marca:{{carro.marca}} Ano:{{carro.ano}}
              <button (click)="infos(carro)">Infos</button>
              <button (click)="removeCar(carro)">Remover</button>
            </li>
        </ul>
     <h3>{{infoCar}}</h3>
</div>
