import { Component } from '@angular/core';
import { Carros } from 'src/app/Carros';
@Component({
  selector: 'app-render-list',
  templateUrl: './render-list.component.html',
  styleUrls: ['./render-list.component.css']
})
export class RenderListComponent {

  carros:Carros[]= [
    {modelo: 'qq', marca: 'audi', ano: 2022},
    {modelo: 'urus', marca: 'lamborghine', ano: 2023},
    {modelo: 'skyline', marca: 'nissan', ano: 2002},
    {modelo: 'uno', marca: 'fiat', ano:2005},
  ]

 carro:Carros = {
  marca: "teste",
  modelo: "qualquer",
  ano: 10,
}

  infoCar = '';

  infos(_carro: Carros): void{
    this.infoCar = ' o modelo ${carro.modelo} da marca ${carros.marca} é do ano de ${carros.ano}'

  }

}
<div class="aula">
        <h1>Meus carros:</h1>
        <ul>
            <li *ngFor="let carro of carros">Modelo: {{carro.modelo}}-{{carro.marca}} <button (click)="infos(carro)">Infos</button></li>
        </ul>
        <h3>{{infoCar}}</h3>


</div>
