import { Component } from '@angular/core';

@Component({
  selector: 'app-diretives',
  templateUrl: './diretives.component.html',
  styleUrls: ['./diretives.component.css']
})
export class DiretivesComponent {
  class = "class"
  comps = ['size', 'color']

}
<h2 [ngStyle]="{color: 'red', 'font-size': '30px', 'font-family': 'Arial'}">diretiva Braba</h2>
<h2 [ngClass]="class">Diretiva de classe</h2>
<h2 [ngClass]="comps">Classe Composta</h2>
