import { style } from '@angular/animations';
import { CssSelector } from '@angular/compiler';
import { Component } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.css']
})
export class EventosComponent {
  cor = "red"
  siz = "35px"
  letraGrande(): void{
    let element = document.querySelector('.tex') as HTMLLIElement

      element.style.color = this.cor
      element.style.fontSize = this.siz
      element.style.width = "250px"


  }
  letraNormal(): void{
    let element = document.querySelector('.tex') as HTMLLIElement

    element.style.fontSize = "16px"
    element.style.color = "black"
    element.style.width = "100px"
    element.innerHTML = "<p>Atenção você é incrivel </p>"

  }
  enviar(): void{
      let div = document.querySelector('.div') as HTMLLIElement
      let text = document.querySelector('.t') as HTMLLIElement

      text.textContent

      div.textContent



  }


}
<div class="aula"> 
    <h1>Events</h1> 
      <p (mouseover)="letraGrande()" (mouseleave)="letraNormal()"  ngClass="tex"> Passa o mouse aki em cima</p>
      <input type="text" value="" ngClass="t">
      <button (click)="enviar()">Enviar</button>
      <div ngClass="div"></div>


</div>
